import React, {Component} from 'react'
import ReactDom from 'react-dom'
import axios from 'axios'
import './QuoteAjax.css'

class QuotesAjax extends Component{

    constructor(props){
        super(props);
        this.state = ''
        this.fetchSympsonsJSON = this.fetchSympsonsJSON.bind(this)
    }

    fetchSympsonsJSON() {
        const characterID = 0 ;
        const url = `https://simpsons-quotes-api.herokuapp.com/quotes`;

        axios.get(url)

            .then(response => response.data[0])
            .then(sympsons => {console.log('data decoded from JSON:', sympsons);

            // Build a block of HTML
            const sympsonsHtml = `
              <div class="Quotes">
                  <blocquote>${sympsons.quote}</blocquote>
              </div>
              <div>
                <img class=${sympsons.characterDirection} src="${sympsons.image}" />
              </div>
              <div>
                  <p><strong>${sympsons.character}</strong></p>
              </div>
            `;
            document.querySelector('#sympsons').innerHTML = sympsonsHtml;
        });
    }
    render(){
        return(
            <div>
                <section class="section">
                    <div class="container">
                        <h1 class="title">Sympsons Quotes</h1>
                        <button>Reload for another quote!</button>
                        <div class="content" id="sympsons">
                                {this.fetchSympsonsJSON()}
                        </div>

                    </div>

  </section>
            </div>
        )

    }
}

export default QuotesAjax
